impl<T, H, const N: usize, const N2: usize> Drop for ConstLru<T, H, N, N2> {
    fn drop(&mut self) {
        // Drop any entries that were allocated
        for entry in self.entries.iter_mut().take(self.free_after as usize) {
            unsafe {
                entry.assume_init_drop();
            }
        }
    }
}
