impl< 'a, T, const N: usize > Drop for State< 'a, T, N > {
            fn drop( &mut self ) {
                if !core::mem::needs_drop::< T >() {
                    return;
                }

                for item in &mut self.slice[ ..self.count ] {
                    unsafe {
                        item.assume_init_drop();
                    }
                }
            }
        }
