impl<T, Alloc: IAlloc> Drop for Box<T, Alloc> {
    fn drop(&mut self) {
        // SAFETY: We own the target of `ptr` and guarantee it is initialized.
        unsafe {
            core::ptr::drop_in_place(self.ptr.as_mut());
        }
        // SAFETY: `this` is immediately forgotten.
        unsafe { self.free() }
    }
}
