impl<T, G> Drop for LockedLazy<T, G> {
            #[inline(always)]
            fn drop(&mut self) {
                if Phased::phase(GenericLockedLazy::sequentializer(&self.__private))
                    .intersects(Phase::INITIALIZED)
                {
                    unsafe { (&*self.__private).get().drop_in_place() }
                }
            }
        }
