pub fn unwrap_mut_with<'a, F>(&'a mut self, mut op: F) -> &'a mut Expr
    where
        F: FnMut(&'a mut Expr) -> Option<&'a mut Expr>,
    {
        let mut cur = self;
        loop {
            match unsafe {
                // Safety: Polonius is not yet stable
                op(transmute::<&mut _, &mut _>(cur))
            } {
                Some(next) => cur = next,
                None => {
                    return cur;
                }
            }
        }
    }
