impl<'a> Drop for Scope<'a> {
    fn drop(&mut self) {
        unsafe {
            ManuallyDrop::drop(&mut self.send);
        }

        let recv = self.recv.take().unwrap();
        let n = tokio::task::block_in_place(move || {
            self.handle.block_on(UnboundedReceiverStream::new(recv).next())
        });
        assert_eq!(n, None);
    }
}
