impl<T> Drop for WeakNode<T> {
    fn drop( &mut self ) {
        if let WeakNode::Piled( PiledWeakNode( node_vec, _ )) = self  {
            unsafe {
                if node_vec.as_ref().ref_cnt.decr() == 0 {
                    drop( Box::from_raw( node_vec.as_ptr() ));
                }
            }
        }
    }
}
