unsafe extern "system" fn GetTrustLevel<T: IUnknownImpl, const OFFSET: isize>(
            this: *mut c_void,
            value: *mut i32,
        ) -> HRESULT {
            unsafe {
                if value.is_null() {
                    return imp::E_POINTER;
                }
                let this = (this as *mut *mut c_void).offset(OFFSET) as *mut T;
                (*this).GetTrustLevel(value)
            }
        }
