pub(crate) fn call_other<T>(hwnd: HWND, message: u32, wparam: WPARAM, lparam: LPARAM) -> LRESULT
where
    T: EventHandler + 'static,
{
    let p = CONTEXT.with(|ctx| *ctx.borrow());
    unsafe {
        let ctx = &mut *p;
        if ctx.event_handler.is_some() {
            let event_handler = ctx
                .event_handler
                .as_mut()
                .unwrap()
                .downcast_mut::<T>()
                .unwrap();
            let ret = event_handler.other(event::Other {
                hwnd,
                message,
                wparam,
                lparam,
            });
            if let Some(ret) = ret {
                LRESULT(ret)
            } else {
                DefWindowProcW(hwnd, message, wparam, lparam)
            }
        } else {
            DefWindowProcW(hwnd, message, wparam, lparam)
        }
    }
}
