fn get_or_insert_with<F: FnOnce(&'b keyexpr) -> T>(self, f: F) -> &'a mut T {
        match self {
            Entry::Vacant(entry) => {
                let value = unsafe { f(core::mem::transmute::<&keyexpr, &keyexpr>(entry.key())) };
                entry.insert(value)
            }
            Entry::Occupied(v) => v.into_mut(),
        }
    }
